        }
        /*end of color options*/

        /*=====FONT FAMILY OPTION=====*/
        /*----------------------------------*/
        /*Site identity font*/
        if( !empty($chrimbo_font_family_site_identity_option) ){
        ?> 
          .site-header .site-title a,
          .site-header .site-description {
                font-family: "<?php echo esc_attr( $chrimbo_font_family_site_identity_option ); ?>";/*"Lato"*/
            }
        <?php
        } 

        /*Title font*/
        if( !empty($chrimbo_font_family_title_option) ){
        ?> 
            h1, h2, h3, h4, h5, h6, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, .wrapper-slider .slide-item .slider-title{
                font-family: "<?php echo esc_attr( $chrimbo_font_family_title_option ); ?>";/*"Lato"*/
            }
        <?php
        } 
        /* Banner Image*/
        if( !empty( $chrimbo_banner_image ) ){
        ?>
            .page-inner-title{
                background-image: url(<?php echo esc_url($chrimbo_banner_image);?>);
                }
        <?php
        }
        // Bail if not WP 4.7.
        $chrimbo_loop_number = 1;
        if ( version_compare( $GLOBALS['wp_version'], '4.7', '<' ) ) {
          $chrimbo_custom_css = $chrimbo_customizer_all_values['chrimbo-custom-css']; 
          $chrimbo_custom_css_output = ''; 
          if ( ! empty( $chrimbo_custom_css ) ) { 
              $chrimbo_custom_css_output .= esc_textarea( $chrimbo_custom_css ) ; 
          } 
         echo $chrimbo_custom_css_output;/*escaping done above*/ 
        } else{
            $chrimbo_customizer_saved_values = chrimbo_get_all_options();
            $chrimbo_custom_css = $chrimbo_customizer_all_values['chrimbo-custom-css'];
            // Bail if there is no Custom CSS.
                if (!empty($chrimbo_custom_css)) {
                    $core_css = wp_get_custom_css();
                    $return = wp_update_custom_css_post( $core_css . $chrimbo_custom_css );
                    if ( ! is_wp_error( $return ) ) {
                    // Remove from theme.
                    $options = esc_textarea($chrimbo_customizer_all_values['chrimbo-custom-css']);
                    echo $options;
                    }
                }
            $chrimbo_custom_css = '';
            $chrimbo_customizer_saved_values['chrimbo-custom-css'] = isset($chrimbo_customizer_defaults['chrimbo-custom-css']) ? $chrimbo_customizer_defaults['chrimbo-custom-css'] : '';
            /*resetting fields*/
            chrimbo_reset_options( $chrimbo_customizer_saved_values );
        }
        ?>
        
        </style>
    <?php
    }
endif;
add_action( 'wp_head', 'chrimbo_wp_head' );